import { useState } from 'react';
import {
  PageWrapper,
  PageTitle,
  PageSubTitle,
  StyledForm,
} from '../../components/shared/CommonStyles';
import { LoginWrapper, LoginTypesContainer, LinkToSignUp } from './style';
import { useHistory } from 'react-router-dom';
import { useContext, useEffect } from 'react';
import UserContext from '../../contexts/UserContext';
import Loader from '../../components/Loader';

// usando login tipo="posto" como posto de vacinacao de tipo="secretaria" como secretaria de saÃºde
export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loginType, setLoginType] = useState('posto');
  const [isLoading, setIsLoading] = useState(true);
  const { token, setToken, isLoggedInType, setIsLoggedInType } =
    useContext(UserContext);
  let history = useHistory();

  useEffect(checkIfItsAlreadyLoggedIn, [token]);

  function checkIfItsAlreadyLoggedIn() {
    if (token) {
      if (isLoggedInType === 'secretaria') {
        history.push('/admin');
      } else {
        history.push('/posto');
      }
    }

    setIsLoading(false);
  }

  function checkCredentials(e) {
    e.preventDefault();
    console.log(email, password, loginType);
    if (email === 'fernando@admin.com' && password === '0000') {
      setToken(1);
      setIsLoggedInType('secretaria');
      history.push('/admin/');
    }
  }

  return (
    <PageWrapper>
      <LoginWrapper>
        isLoading ? (
        <Loader />
        <PageTitle>Bem vindo!</PageTitle>
        <PageSubTitle>
          Realize seu Login como Posto de VacinaÃ§Ã£o ou SecretÃ¡rio(a) de SaÃºde
        </PageSubTitle>
        <StyledForm onSubmit={checkCredentials}>
          <label htmlFor="email">E-mail: </label>
          <input
            type="email"
            id="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <label htmlFor="password">Senha: </label>
          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <LoginTypesContainer>
            <div>
              <input
                type="radio"
                name="login-type"
                id="login-posto"
                value="posto"
                defaultChecked
                onChange={(e) => setLoginType(e.target.value)}
              ></input>
              <label htmlFor="login-posto-vacinacao">Posto de VacinaÃ§Ã£o </label>
            </div>

            <div>
              <input
                type="radio"
                name="login-type"
                id="login-secretaria"
                value="secretaria"
                checked={loginType === 'secretaria'}
                onChange={(e) => setLoginType(e.target.value)}
              ></input>
              <label htmlFor="login-secretaria">Secretaria de SaÃºde</label>
            </div>
          </LoginTypesContainer>

          <input type="submit" disabled={isLoading} value="Login" />
          <LinkToSignUp to="/signup">Cadastre novo Posto</LinkToSignUp>
        </StyledForm>
      </LoginWrapper>
    </PageWrapper>
  );
}
