import styled from 'styled-components';
import { useState } from 'react';
import {
  PageWrapper,
  PageTitle,
  StyledForm,
} from '../../../components/shared/CommonStyles';
import { useContext, useEffect } from 'react';
import UserContext from '../../../contexts/UserContext';
import { getLotsList, getUnitsList } from '../../../services/api';

export default function SendLotToUnit() {
  const [lotsIdList, setLotsIdList] = useState([]);
  const [unitsList, setUnitsList] = useState([]);

  const [idLote, setIdLote] = useState('');
  const [idPosto, setIdPosto] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { token } = useContext(UserContext);

  useEffect(loadLotsAndUnit, [token]);

  function loadLotsAndUnit() {
    const lots = getLotsList({ token });
    const units = getUnitsList({ token });

    setLotsIdList(lots);
    setUnitsList(units);
  }

  function submitInput(e) {
    e.preventDefault();
    console.log(idLote, idPosto);
  }

  return (
    <PageWrapper>
      <SendLotToUnitWrapper>
        <PageTitle> Cadastrar Lote em Posto </PageTitle>
        <StyledForm onSubmit={submitInput}>
          <label htmlFor="idLote"> ID do Lote: </label>
          <input
            type="list"
            id="idLote"
            value={idLote}
            onChange={(e) => setIdLote(e.target.value)}
            required
          />
          <datalist id="names">
            {lotsIdList.map((loteId) => (
              <option value={loteId} />
            ))}
          </datalist>

          <label htmlFor="idPosto"> ID do Posto: </label>
          <input
            type="text"
            id="idPosto"
            value={idPosto}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input type="submit" disabled={isLoading} value="Cadastrar" />
        </StyledForm>{' '}
      </SendLotToUnitWrapper>{' '}
    </PageWrapper>
  );
}

const SendLotToUnitWrapper = styled.div`
  width: 800px;
  @media screen and(max-width:800px) {
    max-width: 100vw;
  }
`;
