import styled from 'styled-components';

const PageWrapper = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const PageTitle = styled.div`
  font-weight: 500;
  font-size: 30px;
  width: 800px;
  display: flex;
  justify-content: center;
  align-items: center;
  margin-bottom: 20px;
`;

export { PageWrapper, PageTitle };
